mapper.select:
select f.*, g.name
from tb_film f
join tb_genre g on (f.genre_id = g.id)
where 1 = 1

	and release_year = #{ releaseYear }


	and production_country = #{ productionCountry }


	and f.genre_id = #{ genre }

		select f.*, g.name, l.user_id
		from tb_film f
		join tb_genre g on (f.genre_id = g.id)
    join tb_like l on (l.target_id = f.id)
		where user_id = #{ mid }

